fetch("https://api.spacexdata.com/v4/launches/upcoming").then(e=>e.json()).then(e=>{const n=Date.parse(e[0].date_utc),t=new Intl.DateTimeFormat("en-GB",{year:"2-digit",day:"numeric",month:"numeric",hour:"numeric",minute:"numeric",timeZone:"UTC"}).format(n);document.getElementById("launch-date").innerHTML=t,document.getElementById("launch-name").innerHTML=e[0].name,document.getElementById("flight-no").innerHTML=e[0].flight_number});